#include <string> #include <iostream> #include <algorithm> #include <queue> using namespace std; int s[200000]; int t[200000]; int main() { int n; priority_queue<int, vector<int>, greater<int>> pq; cin >> n; for (int i = 0; i < n; i++) { cin >> s[i] >> t[i]; } pq.push(t[0]); for (int i = 1; i < n; i++) { if (s[i] >= pq.top()) { pq.pop(); pq.push(t[i]); } else { pq.push(t[i]); } } cout << pq.size(); return (0); }